function Var(name, value){this.Name = name;this.Value= value;}function SessionVars(){this.Vars = new Array();this.nr = 0;this.find = find;this.AddVar = AddVar;this.GetValue = GetValue;this.SetValue = SetValue;this.toStr = toStr;}function find(var_name){var idx;for (idx=0; idx < this.nr; idx++){if (this.Vars[idx].Name == var_name){return idx;}}return -1;}function AddVar(var_name, var_value){var idx = this.find(var_name);if (idx == -1){this.Vars[this.nr] = new Var(var_name, var_value);this.nr++;}}function GetValue(var_name){var idx = this.find(var_name);if (idx == -1){return "undefined variable";}else{return this.Vars[idx].Value;}}function SetValue(var_name, var_value){var idx = this.find(var_name);if (idx == -1){}else{this.Vars[idx].Value = var_value;}}function toStr(){var strVars = "";var i;for (i=0; i < this.nr; i++){strVars += this.Vars[i].Name+"="+this.Vars[i].Value+"&";}return strVars;}
